mapper.select:
SELECT user_id, user_num, user_pwd, user_email, user_phone, user_photo, user_regip, user_state, user_gender, user_loginip, user_regtime, user_isactive, user_cid, college_id, college_des, college_num, college_name FROM sh_user, sh_college
sh_user.user_cid = sh_college.college_id
AND user_id=#{userId}
AND user_num=#{userNum}
AND user_name=#{userName}
AND user_phone=#{userPhone}
AND user_email=#{userEmail}
AND user_pwd=#{userPwd}

mapper.update:
UPDATE sh_user
SET user_state = -1
WHERE user_id=#{userId}

UPDATE sh_user
SET user_state = 1
WHERE user_id=#{userId}

UPDATE sh_user
SET user_photo =#{userPhoto}
WHERE
    user_id =#{userId}

UPDATE sh_user
SET user_pwd =#{userPwd}
WHERE
    user_email =#{userEmail}
